import React from 'react';
import './App.css';

function Modal({ children, isOpen, onClose }) {
  if (!isOpen) {
    return null;
  }

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()} onClick={onClose}>
        닫기
      </div>
    </div>
  );
}

export default Modal;
